import {createSlice} from "@reduxjs/toolkit";

const initialState = {
    batteryType: "megapack",
    landWidth: 0,
    landHeight: 0,
    budget: 0,

}

const estimator = createSlice({
    name:"estimator",
    state:initialState,
    reducers:{
        showDetails: (state) => {
            state.batteryType = !state.batteryType
        },  
    },
});
const batterySlice = createSlice({
    name: 'battery',
    initialState,
    reducers: {
      setBatteryType: (state, action) => {
        state.batteryType = action.payload;
      },
    },
});

export const {showDetails} = estimator.actions

export default estimator.reducer
