import {createSlice} from "@reduxjs/toolkit";

const initialState = {
    batteryType: "megapack",
    landWidth: 0,
    landHeight: 0,
    budget: 0,

}

const batterySlice = createSlice({
    name: 'battery',
    initialState,
    reducers: {
      setBatteryType: (state, action) => {
        switch(action.type){
            case "SET_BATTERY_TYPE":
                state.batteryType = action.payload;
        }
      },
    },
});

export const { setBatteryDetails } = batterySlice.actions;

export default batterySlice.reducer
