    let landHeight = battery.batteryFloorHeight * noOfBatteries;
    let landWidth = battery.batteryFloorWidth * noOfBatteries;
    let siteEnergy = battery.en
    return ({
        price : price,
        landHeight:landHeight,
        landWidth:landWidth,
        siteEnergy:0
