import React, {useRef, useState, FormEvent } from 'react';
import { FormControl, MenuItem, InputLabel, Select, TextField } from '@mui/material';
import { styled } from '@mui/system';
import {useDispatch} from 'react-redux';
import { setBatteryType } from '../reducers/reducer';
import battery_types from '../util/Battery';
import "./css/InputDetailsPanel.css";


const FormPane: React.FC = () => {
    const dispatch = useDispatch();
    const formValuesRef = useRef<{ [key: string]: string }>({}); // State to hold form values

    const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        const { name, value } = event.target;
        formValuesRef.current[name] = value;
    };

    const handleSubmit = (event: FormEvent) => {
        event.preventDefault();
        // Perform calculations or handle form submission logic here
    };

    const StyledContainer = styled('div')({
        display: 'flex',
        flexDirection: 'column',
        margin: '10px',
    });

    const [selectedOption, setSelectedOption] = useState('');

    const handleOptionChange = (event: { target: { value: React.SetStateAction<string>; }; }) => {
        setSelectedOption(event.target.value);
        dispatch(setBatteryType(event.target.value))
    };

    const handleNoofBatteryChange = (event: { target: { value: React.SetStateAction<string>; }; }) => {
        setSelectedOption(event.target.value);
        dispatch(setBatteryType(event.target.value))
    };

    return (
        <div className='inputDetailsPanel'>
            <form onSubmit={handleSubmit}>
                <StyledContainer>
                    <FormControl>
                        <InputLabel id="select-label">Type Of Battery</InputLabel>
                        <Select
                            labelId="select-label"
                            value={selectedOption}
                            onChange={handleOptionChange}
                        >
                            {battery_types && battery_types.map((battery_item) => (
                                <MenuItem value={battery_item.battery_name}>{battery_item.battery_name}</MenuItem>
                            ))}
                        </Select>
                    </FormControl>
                    <TextField
                        name="noofbatteries"
                        label="Number Of Batteries"
                        onChange={handleNoofBatteryChange} required
                    />
                    <TextField
                        name="budget"
                        label="Whats Your Budget"
                        onChange={handleChange} required
                    />
                    <TextField
                        name="landHeight"
                        label="Land Height"
                        onChange={handleChange}
                        required
                    />
                    <TextField
                        name="landWidth"
                        label="Land Width"
                        onChange={handleChange}
                        required
                    />
                    <TextField
                        name="requiredEnergy"
                        label="Required Energy"
                        onChange={handleChange}
                        required
                    />
                </StyledContainer>
            </form>
        </div>
    );
};

export default FormPane;
