import {createSlice} from "@reduxjs/toolkit";

const initialState = {
    batteryType: "megapack",
    landWidth: 0,
    landHeight: 0,
    budget: 0,

}

const estimator = createSlice({
    name:"estimator",
    state:initialState,
    reducers:{
        showDetails: (state) => {
            state.batteryType = !state.batteryType
        },  
    },
});
