import React, { useState, ChangeEvent, FormEvent } from 'react';
import { Menu, MenuItem, TextField, Button } from '@mui/material';
import { styled } from '@mui/system';

const FormPane: React.FC = () => {
    const [formValues, setFormValues] = useState<{ [key: string]: string }>({}); // State to hold form values

    const handleChange = (event: ChangeEvent<HTMLInputElement>) => {
        setFormValues((prevValues) => ({
            ...prevValues,
            [event.target.name]: event.target.value,
        }));
    };

    const handleSubmit = (event: FormEvent) => {
        event.preventDefault();
        // Perform calculations or handle form submission logic here
    };

    const StyledContainer = styled('div')({
        display: 'flex',
        flexDirection: 'column',
    });

    const [anchorEl, setAnchorEl] = useState(null);

    const handleClick = (event: { currentTarget: React.SetStateAction<null>; }) => {
        setAnchorEl(event.currentTarget);
    };

    const handleClose = () => {
        setAnchorEl(null);
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <StyledContainer>
                    <Menu
                        anchorEl={anchorEl}
                        open={Boolean(anchorEl)}
                        onClose={handleClose}
                    >
                        <MenuItem onClick={handleClose}>Option 1</MenuItem>
                        <MenuItem onClick={handleClose}>Option 2</MenuItem>
                        <MenuItem onClick={handleClose}>Option 3</MenuItem>
                    </Menu>

                    <TextField
                        name="budget"
                        label="Whats Your Budget"
                        value={formValues.budget || ''}
                        onChange={handleChange}
                    />
                    <TextField
                        name="landHeight"
                        label="Land Height"
                        value={formValues.landHeight || ''}
                        onChange={handleChange}
                    />
                    <TextField
                        name="landWidth"
                        label="Land Width"
                        value={formValues.landWidth || ''}
                        onChange={handleChange}
                    />
                    <TextField
                        name="requiredEnergy"
                        label="Required Energy"
                        value={formValues.requiredEnergy || ''}
                        onChange={handleChange}
                    />
                    {/* Add more form fields as needed */}
                    <Button type="submit" variant="contained" color="primary">
                        Calculate
                    </Button>
                </StyledContainer>
            </form>
        </div>
    );
};

export default FormPane;
