import React, { useState, ChangeEvent, FormEvent } from 'react';
import { FormControl, MenuItem, InputLabel, Select, TextField, Button } from '@mui/material';
import { styled } from '@mui/system';

const FormPane: React.FC = () => {
    const [formValues, setFormValues] = useState<{ [key: string]: string }>({}); // State to hold form values

    const handleChange = (event: ChangeEvent<HTMLInputElement>) => {
        setFormValues((prevValues) => ({
            ...prevValues,
            [event.target.name]: event.target.value,
        }));
    };

    const handleSubmit = (event: FormEvent) => {
        event.preventDefault();
        // Perform calculations or handle form submission logic here
    };

    const StyledContainer = styled('div')({
        display: 'flex',
        flexDirection: 'column',
    });

    const [selectedOption, setSelectedOption] = useState('');

    const handleOptionChange = (event) => {
        setSelectedOption(event.target.value);
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <StyledContainer>
                    <FormControl>
                        <InputLabel id="select-label">Dropdown</InputLabel>
                        <Select
                            labelId="select-label"
                            value={selectedOption}
                            onChange={handleOptionChange}
                        >
                            <MenuItem value="option1">Option 1</MenuItem>
                            <MenuItem value="option2">Option 2</MenuItem>
                            <MenuItem value="option3">Option 3</MenuItem>
                        </Select>
                    </FormControl>
                    <TextField
                        name="typeOfBattery"
                        label="Type Of Battery"
                        value={formValues.typeOfBattery || ''}
                        onChange={handleChange}
                    />
                    <TextField
                        name="budget"
                        label="Whats Your Budget"
                        value={formValues.budget || ''}
                        onChange={handleChange}
                    />
                    <TextField
                        name="landHeight"
                        label="Land Height"
                        value={formValues.landHeight || ''}
                        onChange={handleChange}
                    />
                    <TextField
                        name="landWidth"
                        label="Land Width"
                        value={formValues.landWidth || ''}
                        onChange={handleChange}
                    />
                    <TextField
                        name="requiredEnergy"
                        label="Required Energy"
                        value={formValues.requiredEnergy || ''}
                        onChange={handleChange}
                    />
                    {/* Add more form fields as needed */}
                    <Button type="submit" variant="contained" color="primary">
                        Calculate
                    </Button>
                </StyledContainer>
            </form>
        </div>
    );
};

export default FormPane;
