import React from 'react';
import { useSelector } from 'react-redux';
import getEstimation from '../util/PanelCalculator'
import { Table, TableBody, TableRow, TableCell , Typography, Box, Paper } from '@mui/material';


const PlanLayoutPanel = () => {
    const battery = useSelector((state:any) => state.battery);

    const estimation = getEstimation(battery,10);

    return (
        <>
              <Box m={2}>
        <Paper elevation={3} variant="outlined">
          <Box p={2}>
            <Typography variant="h6" gutterBottom>
              {"Estimation Details"}
            </Typography>
            <Typography variant="body1">
            <Table>
      <TableBody>
        <TableRow>
          <TableCell>{"Land Required for Site:- "}</TableCell>
          <TableCell>{estimation.landHeight + "FT X " + estimation.landWidth + "FT"}</TableCell>
        </TableRow>
        <TableRow>
          <TableCell>{"Site Energy:- "}</TableCell>
          <TableCell>{estimation.siteEnergy + " MWh"}</TableCell>
        </TableRow>
        <TableRow>
          <TableCell>{"Cost:- "}</TableCell>
          <TableCell>{"$" + estimation.price}</TableCell>
        </TableRow>
        <TableRow>
          <TableCell>{"No of Transformers Required:- "}</TableCell>
          <TableCell>{estimation.noOfTransformers}</TableCell>
        </TableRow>
      </TableBody>
    </Table>
            </Typography>
            {/* Add more Typography components or other content as needed */}
          </Box>
        </Paper>
      </Box>
        {estimation.landWidth}
        {estimation.noOfTransformers}
        {estimation.price}
        {estimation.siteEnergy}

        {/* show the price, land dimension required and the sites energy density */}
        {}
        </>
    );
}

export default PlanLayoutPanel;
