import React from 'react';
import { useSelector } from 'react-redux';
import getEstimation from '../util/PanelCalculator'
import { Grid, Typography, Box, Paper } from '@mui/material';


const EstimationDetailsPanel = () => {
    const battery = useSelector((state: any) => state.battery);

    const estimation = getEstimation(battery, 10);

    return (
        <>
<Box m={2}>
  <Paper>
    <Box p={2}>
      <Typography variant="h6" gutterBottom>
        {"Estimation Details"}
      </Typography>
      <Grid container spacing={2}>
        <Grid item xs={6} style={{ border: '1px solid black', padding: '8px' }}>
          <strong>{"Land Required for Site:- "}</strong>
        </Grid>
        <Grid item xs={6} style={{ border: '1px solid black', padding: '8px' }}>
          {estimation.landHeight + "FT X " + estimation.landWidth + "FT"}
        </Grid>
        <Grid item xs={6} style={{ border: '1px solid black', padding: '8px' }}>
          <strong>{"Site Energy:- "}</strong>
        </Grid>
        <Grid item xs={6} style={{ border: '1px solid black', padding: '8px' }}>
          {estimation.siteEnergy + " MWh"}
        </Grid>
        <Grid item xs={6} style={{ border: '1px solid black', padding: '8px' }}>
          <strong>{"Cost:- "}</strong>
        </Grid>
        <Grid item xs={6} style={{ border: '1px solid black', padding: '8px' }}>
          {"$" + estimation.price}
        </Grid>
        <Grid item xs={6} style={{ border: '1px solid black', padding: '8px' }}>
          <strong>{"No of Transformers Required:- "}</strong>
        </Grid>
        <Grid item xs={6} style={{ border: '1px solid black', padding: '8px' }}>
          {estimation.noOfTransformers}
        </Grid>
      </Grid>
    </Box>
  </Paper>
</Box>
        </>
    );
}

export default EstimationDetailsPanel;
